$(document).ready(function(){$("#contactMail input,#contactMail textarea").focus(function(){$(this).removeClass("input-field-bottom-red")});$("#submit-contact").click(function(){var f=/^[A-Z0-9._%+-]+@([A-Z0-9-]+\.)+[A-Z]{2,4}$/i;var d=/^(((\+|00)( )?[0-9]{1,3})|0)[-. ]?([1-9]([-. ]?[0-9]{2}){4})|([-. ]?[1-9][0-9]{2}([-. ]?[0-9]{3}){2})$/i;var a="";var c=$("input.nom").val();var h=$("input.prenom").val();var e=$("input.email").val();var b=$("input.phone").val();var g=$("textarea.message").val();if(h==""){a=a+'<span>Le champ "Prénom" est vide</span><br/>';$("input.prenom").addClass("input-field-bottom-red")}if(e==""){a=a+'<span>Le champ "Email" est vide</span><br/>';$("input.email").addClass("input-field-bottom-red")}else{if(!f.test(e)){a=a+'<span>Le format de "E-mail" est incorrect</span><br/>';$("input.email").addClass("input-field-bottom-red")}}if(c==""){a=a+'<span>Le champ "Nom" est vide</span><br/>';$("input.nom").addClass("input-field-bottom-red")}if(b==""){a=a+'<span>Le champ "Téléphone" est vide</span><br/>';$("input.phone").addClass("input-field-bottom-red")}else{if(!d.test(b)){a=a+'<span>Le format de "Téléphone" est incorrect</span><br/>';$("input.phone").addClass("input-field-bottom-red")}}if(g==""){a=a+'<span>Le champ "Message" est vide</span><br/>';$("textarea.message").addClass("input-field-bottom-red")}if(a!=""){$(".error_list").html(a);return false}else{$("#contactMail").submit();return false}})});